import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [reports, setReports] = useState([]);
  const [vitals, setVitals] = useState([]);
  const [sharedReports, setSharedReports] = useState([]);
  const [activeTab, setActiveTab] = useState('reports');
  const [uploadFile, setUploadFile] = useState(null);
  const [reportType, setReportType] = useState('');
  const [reportDate, setReportDate] = useState('');
  const [reportVitals, setReportVitals] = useState('');
  const [vitalType, setVitalType] = useState('');
  const [vitalValue, setVitalValue] = useState('');
  const [vitalDate, setVitalDate] = useState('');
  const [shareEmail, setShareEmail] = useState('');
  const [shareReportId, setShareReportId] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [filterVitalType, setFilterVitalType] = useState('');
  const [filterReportType, setFilterReportType] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    const userData = JSON.parse(localStorage.getItem('user'));
    setUser(userData);
    fetchReports();
    fetchVitals();
    fetchSharedReports();
  }, [navigate]);

  const fetchReports = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/reports', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setReports(response.data);
    } catch (err) {
      console.error('Failed to fetch reports', err);
    }
  };

  const fetchVitals = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/vitals', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setVitals(response.data);
    } catch (err) {
      console.error('Failed to fetch vitals', err);
    }
  };

  const fetchSharedReports = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/shared-reports', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSharedReports(response.data);
    } catch (err) {
      console.error('Failed to fetch shared reports', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!uploadFile) return;

    const formData = new FormData();
    formData.append('file', uploadFile);
    formData.append('report_type', reportType);
    formData.append('date', reportDate);
    formData.append('vitals', reportVitals);

    try {
      await axios.post('http://localhost:5000/api/reports/upload', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      fetchReports();
      setUploadFile(null);
      setReportType('');
      setReportDate('');
      setReportVitals('');
    } catch (err) {
      console.error('Upload failed', err);
    }
  };

  const handleAddVital = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/vitals', {
        type: vitalType,
        value: parseFloat(vitalValue),
        date: vitalDate
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      fetchVitals();
      setVitalType('');
      setVitalValue('');
      setVitalDate('');
    } catch (err) {
      console.error('Add vital failed', err);
    }
  };

  const handleShare = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:5000/api/reports/${shareReportId}/share`, {
        shared_with_email: shareEmail,
        access_type: 'read'
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      alert('Report shared successfully');
      setShareEmail('');
      setShareReportId('');
    } catch (err) {
      console.error('Share failed', err);
    }
  };

  const filteredReports = reports.filter(report => {
    if (filterDate && report.date !== filterDate) return false;
    if (filterVitalType && !report.vitals?.includes(filterVitalType)) return false;
    if (filterReportType && report.report_type !== filterReportType) return false;
    return true;
  });

  return (
    <div className="dashboard">
      <header>
        <h1>Health Wallet</h1>
        <button onClick={handleLogout}>Logout</button>
      </header>
      <nav>
        <button onClick={() => setActiveTab('reports')}>My Reports</button>
        <button onClick={() => setActiveTab('vitals')}>Vitals</button>
        <button onClick={() => setActiveTab('shared')}>Shared Reports</button>
      </nav>
      <main>
        {activeTab === 'reports' && (
          <div>
            <h2>My Reports</h2>

            {/* Upload Form */}
            <form onSubmit={handleUpload} className="upload-form">
              <h3>Upload New Report</h3>
              <input
                type="file"
                onChange={(e) => setUploadFile(e.target.files[0])}
                required
              />
              <input
                type="text"
                placeholder="Report Type (e.g., Blood Test)"
                value={reportType}
                onChange={(e) => setReportType(e.target.value)}
                required
              />
              <input
                type="date"
                value={reportDate}
                onChange={(e) => setReportDate(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="Vitals (e.g., BP: 120/80)"
                value={reportVitals}
                onChange={(e) => setReportVitals(e.target.value)}
              />
              <button type="submit">Upload Report</button>
            </form>

            {/* Filters */}
            <div className="filters">
              <h3>Filter Reports</h3>
              <input
                type="date"
                placeholder="Filter by Date"
                value={filterDate}
                onChange={(e) => setFilterDate(e.target.value)}
              />
              <input
                type="text"
                placeholder="Filter by Vital Type"
                value={filterVitalType}
                onChange={(e) => setFilterVitalType(e.target.value)}
              />
              <input
                type="text"
                placeholder="Filter by Report Type"
                value={filterReportType}
                onChange={(e) => setFilterReportType(e.target.value)}
              />
            </div>

            {/* Share Form */}
            <form onSubmit={handleShare} className="share-form">
              <h3>Share Report</h3>
              <input
                type="number"
                placeholder="Report ID"
                value={shareReportId}
                onChange={(e) => setShareReportId(e.target.value)}
                required
              />
              <input
                type="email"
                placeholder="Share with Email"
                value={shareEmail}
                onChange={(e) => setShareEmail(e.target.value)}
                required
              />
              <button type="submit">Share Report</button>
            </form>

            <ul>
              {filteredReports.map(report => (
                <li key={report.id}>
                  {report.original_name} - {report.report_type} - {report.date}
                  <a href={`http://localhost:5000/api/reports/${report.id}/download`} target="_blank" rel="noopener noreferrer">Download</a>
                  <span> ID: {report.id}</span>
                </li>
              ))}
            </ul>
          </div>
        )}
        {activeTab === 'vitals' && (
          <div>
            <h2>Vitals</h2>

            {/* Add Vital Form */}
            <form onSubmit={handleAddVital} className="vital-form">
              <h3>Add New Vital</h3>
              <input
                type="text"
                placeholder="Vital Type (e.g., Blood Pressure)"
                value={vitalType}
                onChange={(e) => setVitalType(e.target.value)}
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Value"
                value={vitalValue}
                onChange={(e) => setVitalValue(e.target.value)}
                required
              />
              <input
                type="date"
                value={vitalDate}
                onChange={(e) => setVitalDate(e.target.value)}
                required
              />
              <button type="submit">Add Vital</button>
            </form>

            <ul>
              {vitals.map(vital => (
                <li key={vital.id}>
                  {vital.type}: {vital.value} on {vital.date}
                </li>
              ))}
            </ul>
          </div>
        )}
        {activeTab === 'shared' && (
          <div>
            <h2>Shared Reports</h2>
            <ul>
              {sharedReports.map(report => (
                <li key={report.id}>
                  {report.original_name} from {report.owner_username}
                  <a href={`http://localhost:5000/api/reports/${report.id}/download`} target="_blank" rel="noopener noreferrer">Download</a>
                </li>
              ))}
            </ul>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
